import React, { Component } from 'react';
import ShelfComponent from '../components/ShelfComponent';
import AddBookComponent from '../components/AddBookComponent';
import {getAll} from '../BooksAPI';

export default class LibraryView extends Component{
    async componentDidMount(){
        try {
            const allBooks = await getAll();
            const currentlyReading = allBooks.filter(book => book.ShelfComponent === 'currentlyReading');
            const wantToRead = allBooks.filter(book => book.ShelfComponent === 'wantToRead');
            const read = allBooks.filter(book => book.ShelfComponent === 'read');
            console.log(allBooks)
        } catch(error) {
            console.error('issue with getting all books');
        }
    }

    render(){
        return(
            <div>
                <div className="list-books">
                    <div className="list-books-title">
                    <h1>MyReads</h1>
                    </div>
                    <div className="list-books-content">
                        <div>
                            <ShelfComponent 
                                category="Currently Reading"
                            />
                            <ShelfComponent 
                                category="Want to Read"
                            />
                            <ShelfComponent 
                                category="Read"
                            />
                        </div>
                    </div>
                    <AddBookComponent />
                </div>
            </div>
        )
    }
}
